      popWidth1: 400,
      popCol1:'50%',
      popWidth2: 300,
      popCol2:'100%',
      timer: false
    }
  },
  mounted () {
    this.initData()
    this.popoverWidth(); // 组件初始化的时候不会触发onresize事件，这里强制执行一次
    window.onresize = () => {
      if(!this.timer){ // 使用节流机制，降低函数被触发的频率
        this.timer = true;
        let that = this; // 匿名函数的执行环境具有全局性，为防止this丢失这里用that变量保存一下
        setTimeout(function(){
          that.popoverWidth();
          that.timer = false;
        },400)
      }
    }
  },
	destroyed() {
		// 组件销毁后解绑事件
		window.onresize = null;
	},
  created () {
    this.chargeList()
    // console.log(this.serviceList)
  },
  methods: {
    initData() {
      // var x = document.cookie;
       const res =  fetch("https://cyan-dev.site.youdao.com/api/v1/service/serviceList", {
          method: 'GET', // *GET, POST, PUT, DELETE, etc.
          mode: 'cors', // no-cors, *cors, same-origin
          cache: 'no-cache', // *default, no-cache, reload, force-cache, only-if-cached
          credentials: 'include', // include, *same-origin, omit
          headers: {
            'Content-Type': 'application/json',
            // 'Content-Type': 'application/x-www-form-urlencoded',
          },
          redirect: 'follow', // manual, *follow, error
          referrerPolicy: 'no-referrer', // no-referrer, *no-referrer-when-downgrade, origin, origin-when-cross-origin, same-origin, strict-origin, strict-origin-when-cross-origin, unsafe-url
          body: undefined // body data type must match "Content-Type" header
      });
      console.log(res)
    },
    handleServiceSearch(value) {
      if(!value||value==''){
        this.serviceOptions = this.serviceList
      } else {
        let tmpList = []
        this.serviceList.forEach((item) => {
          if (item.name.toString().toUpperCase().indexOf(value.toUpperCase()) !== -1) {
            tmpList.push({
              name: item.name,
              desc: item.desc,
              url: item.url
            })
          } else if (item.desc.toString().toUpperCase().indexOf(value.toUpperCase()) !== -1) {
            tmpList.push({
              name: item.name,
              desc: item.desc,
              url: item.url
            })
          } 
        })
        this.serviceOptions = tmpList
      }
    },
    chargeList() {
      let tmpList = {}
      this.serviceType.forEach(item => {
        tmpList[item.class_name] = []
      })
      this.serviceList.forEach((item) => {
        tmpList[item.class_name].push(item)
      })
      this.list = tmpList
      this.serviceOptions = this.serviceList
    },
		popoverWidth() {
      let w = window.innerWidth;
      let h = window.innerHeight;
      if(h <= 500 && h > 20) {
        this.popoHeight = h-20
      } else if(h > 500) {
        this.popoHeight = 500
      } else {
        this.popoHeight = 0
      }if(w <= 1020) {// 1列
        this.popWidth1 = 400
        this.popCol1 = '100%'
        this.popWidth2 = 300
        this.popCol2 = '100%'
      } else if(w <= 1280) {// 2列
        this.popWidth1 = (w - 1020)/2 + 700
        this.popCol1 = '50%'
        this.popWidth2 = 550
        this.popCol2 = '50%'
      } else if (w <= 1520) {// 3列
        this.popWidth1 = (w - 1280)/2 + 1000
        this.popCol1 = '33%'
        this.popWidth2 = 600
        this.popCol2 = '50%'
      } else if (w <= 2080) {// 4列
        this.popWidth1 = (w - 1520)/2 + 1250
        this.popCol1 = '25%'
        this.popWidth2 = 600
        this.popCol2 = '50%'
      }  else {
        this.popWidth1 = 1200
        this.popCol1 = '25%'
        this.popWidth2 = 600
        this.popCol2 = '50%'
      }
    },
    handleUrl(url){
      window.open(url, '_blank')
    },
