<script>
import './header-bar.less'
import popover from '@/components/popover.vue'
import AutoComplete from '@/components/autoComplete.vue'
import Vue from 'vue'
import VueMatomo from 'vue-matomo'
Vue.use(VueMatomo, {
  host: 'https://matomo.corp.youdao.com/',
  siteId: '3', // siteId值
  // 根据router自动注册,需要注意的是如果有路由传值值太长的话会matomo会监听不到并报414，就不能使用此方法了
  //  router: router,
  // 是否需要在发送追踪信息之前请求许可
  // 默认false
  requireConsent: false,
  enableLinkTracking: true,
  // 是否追踪初始页面
  // 默认true
  trackInitialView: false,
  // 最终的追踪js文件名，因为我这边的matomo版本比较老，所以使用的是piwik，现在版本新的话此值应该为matomo
  trackerFileName: 'matomo',
  debug: true
})
export default {
  name: 'zhimaHeader',
  props:{
    propList: {
      type: Array,
      // required: true,
      default: () => { return [] }
    },
    propType: {
      type: Array,
      // required: true,
      default: () => { return [] }
    }
  },
  components:{
    popover,
    AutoComplete
  },
  data () {
    return {
      // docSearch: '',
      // searchList: [],
      serviceList: [],
      serviceType: [],
      serviceOptions:[],
      list:[],
      popoHeight: 500,
      popWidth1: 400,
      popCol1:'50%',
      popWidth2: 300,
      popCol2:'100%',
      timer: false
    }
  },
  mounted () {
    this.chargeList()
    this.popoverWidth(); // 组件初始化的时候不会触发onresize事件，这里强制执行一次
    window.onresize = () => {
      if(!this.timer){ // 使用节流机制，降低函数被触发的频率
        this.timer = true;
        let that = this; // 匿名函数的执行环境具有全局性，为防止this丢失这里用that变量保存一下
        setTimeout(function(){
          that.popoverWidth();
          that.timer = false;
        },400)
      }
    }
  },
	destroyed() {
		// 组件销毁后解绑事件
		window.onresize = null;
	},
  created () {
  },
  methods: {
    async initData() {
      if(this.propList.length==0||this.propType.length==0){
       const response1 = await fetch("https://zhima.inner.youdao.com/api/v1/open/serviceList", {
          method: 'GET', // *GET, POST, PUT, DELETE, etc.
          mode: 'cors', // no-cors, *cors, same-origin
          cache: 'no-cache', // *default, no-cache, reload, force-cache, only-if-cached
          credentials: 'omit', // include, *same-origin, omit
          headers: {
            'Content-Type': 'application/json',
            // 'Content-Type': 'application/x-www-form-urlencoded',
          },
          redirect: 'follow', // manual, *follow, error
          referrerPolicy: 'no-referrer', // no-referrer, *no-referrer-when-downgrade, origin, origin-when-cross-origin, same-origin, strict-origin, strict-origin-when-cross-origin, unsafe-url
          // body: undefined // body data type must match "Content-Type" header
      })
       const response2 = await fetch("https://zhima.inner.youdao.com/api/v1/open/serviceType", {
          method: 'GET',
          mode: 'cors',
          cache: 'no-cache',
          credentials: 'omit',
          headers: {
            'Content-Type': 'application/json',
          },
          redirect: 'follow',
          referrerPolicy: 'no-referrer',
      })
        this.serviceList = (await response1.json()).data
        this.serviceType = (await response2.json()).data
        if(!this.serviceList||!this.serviceType){
          this.serviceList = []
          this.serviceType = []
        }
      }else {
        this.serviceList = this.propList
        this.serviceType = this.propType
      }
    },
    handleServiceSearch(value) {
      if(!value||value==''){
        this.serviceOptions = this.serviceList
      } else {
        let tmpList = []
        this.serviceList.forEach((item) => {
          if (item.name.toString().toUpperCase().indexOf(value.toUpperCase()) !== -1) {
            tmpList.push({
              name: item.name,
              desc: item.desc,
              url: item.url
            })
          } else if (item.desc.toString().toUpperCase().indexOf(value.toUpperCase()) !== -1) {
            tmpList.push({
              name: item.name,
              desc: item.desc,
              url: item.url
            })
          } 
        })
        this.serviceOptions = tmpList
      }
    },
    async chargeList() {
      await this.initData()
      let tmpList = {}
      this.serviceType.forEach(item => {
        tmpList[item.class_name] = []
      })
      this.serviceList.forEach((item) => {
        tmpList[item.class_name].push(item)
      })
      this.list = tmpList
      this.serviceOptions = this.serviceList
    },
		popoverWidth() {
      let w = window.innerWidth;
      let h = window.innerHeight;
      if(h <= 500 && h > 20) {
        this.popoHeight = h-60
      } else if(h > 500) {
        this.popoHeight = h*5/6 - 80
      } else {
        this.popoHeight = 0
      }if(w <= 1020) {// 1列
        this.popWidth1 = 400
        this.popCol1 = '100%'
        this.popWidth2 = 300
        this.popCol2 = '100%'
      } else if(w <= 1280) {// 2列
        this.popWidth1 = (w - 1020)/2 + 700
        this.popCol1 = '50%'
        this.popWidth2 = 550
        this.popCol2 = '50%'
      } else if (w <= 1520) {// 3列
        this.popWidth1 = (w - 1280)/2 + 1000
        this.popCol1 = '33%'
        this.popWidth2 = 600
        this.popCol2 = '50%'
      } else if (w <= 2080) {// 4列
        this.popWidth1 = (w - 1520)/2 + 1300
        this.popCol1 = '25%'
        this.popWidth2 = 600
        this.popCol2 = '50%'
      } else if (w <= 2620) {// 5列
        this.popWidth1 = (w - 2080)/2 + 1850
        this.popCol1 = '20%'
        this.popWidth2 = 600
        this.popCol2 = '50%'
      }   else {
        this.popWidth1 = (w - 2620)/2 + 1850
        this.popCol1 = '20%'
        this.popWidth2 = 600
        this.popCol2 = '50%'
      }
    },
    handleUrl(value){
      if(!value.url||value.url==''){
        this.$Notice.info({
            title: '当前服务暂不支持，请尝试POPO答疑',
            desc: ''
        });
      } else {
        this.$matomo.trackEvent("点击链接", value.name, value.url)
        window.open(value.url, '_blank')
      }
    },
    // handleSelectDoc (value) {
    //   for (let i = 0; i < this.serviceList.length; i++) {
    //     const item = this.serviceList[i];
    //     if(item.name == value){
    //       window.open(item.doc, '_blank')
    //       break
    //     }
    //   }
    // },
    // handleSearchDoc (value) {
    //   let tmpList = []
    //   this.serviceList.forEach((item) => {
    //     if (item.name.toString().toUpperCase().indexOf(value.toUpperCase()) !== -1) {
    //       tmpList.push({
    //         name: item.name,
    //         desc: item.desc
    //       })
    //     }
    //   })
    //   this.searchList = tmpList
    // }
  }
}
</script>
